CREATE TABLE `kkp`.`jenis_cetakan` ( `id` INT NOT NULL AUTO_INCREMENT , `nama` VARCHAR(100) NOT NULL , `is_fixed` INT NOT NULL , `is_active` INT NOT NULL DEFAULT '1' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;